unit.typedef:
typedef enum AACCoder {

AAC_CODER_ANMR = 0,

AAC_CODER_TWOLOOP,

AAC_CODER_FAST,

AAC_CODER_NB,

}AACCoder;

typedef struct AACEncOptions {

int coder;

int pns;

int tns;

int ltp;

int pce;

int pred;

int mid_side;

int intensity_stereo;

} AACEncOptions;

typedef struct AACCoefficientsEncoder {

void (*search_for_quantizers)(AVCodecContext *avctx, struct AACEncContext *s,

SingleChannelElement *sce, const float lambda);

void (*encode_window_bands_info)(struct AACEncContext *s, SingleChannelElement *sce,

int win, int group_len, const float lambda);

void (*quantize_and_encode_band)(struct AACEncContext *s, PutBitContext *pb, const float *in, float *out, int size,

int scale_idx, int cb, const float lambda, int rtz);

void (*encode_tns_info)(struct AACEncContext *s, SingleChannelElement *sce);

void (*encode_ltp_info)(struct AACEncContext *s, SingleChannelElement *sce, int common_window);

void (*encode_main_pred)(struct AACEncContext *s, SingleChannelElement *sce);

void (*adjust_common_pred)(struct AACEncContext *s, ChannelElement *cpe);

void (*adjust_common_ltp)(struct AACEncContext *s, ChannelElement *cpe);

void (*apply_main_pred)(struct AACEncContext *s, SingleChannelElement *sce);

void (*apply_tns_filt)(struct AACEncContext *s, SingleChannelElement *sce);

void (*update_ltp)(struct AACEncContext *s, SingleChannelElement *sce);

void (*ltp_insert_new_frame)(struct AACEncContext *s);

void (*set_special_band_scalefactors)(struct AACEncContext *s, SingleChannelElement *sce);

void (*search_for_pns)(struct AACEncContext *s, AVCodecContext *avctx, SingleChannelElement *sce);

void (*mark_pns)(struct AACEncContext *s, AVCodecContext *avctx, SingleChannelElement *sce);

void (*search_for_tns)(struct AACEncContext *s, SingleChannelElement *sce);

void (*search_for_ltp)(struct AACEncContext *s, SingleChannelElement *sce, int common_window);

void (*search_for_ms)(struct AACEncContext *s, ChannelElement *cpe);

void (*search_for_is)(struct AACEncContext *s, AVCodecContext *avctx, ChannelElement *cpe);

void (*search_for_pred)(struct AACEncContext *s, SingleChannelElement *sce);

} AACCoefficientsEncoder;

typedef struct AACQuantizeBandCostCacheEntry {

float rd;

float energy;

int bits;

char cb;

char rtz;

uint16_t generation;

} AACQuantizeBandCostCacheEntry;

typedef struct AACPCEInfo {

int64_t layout;

int num_ele[4]; 

int pairing[3][8]; 

int index[4][8]; 

uint8_t config_map[16]; 

uint8_t reorder_map[16]; 

} AACPCEInfo;

typedef struct AACEncContext {

AVClass *av_class;

AACEncOptions options; 

PutBitContext pb;

FFTContext mdct1024; 

FFTContext mdct128; 

AVFloatDSPContext *fdsp;

AACPCEInfo pce; 

float *planar_samples[16]; 

int profile; 

int needs_pce; 

LPCContext lpc; 

int samplerate_index; 

int channels; 

const uint8_t *reorder_map; 

const uint8_t *chan_map; 

ChannelElement *cpe; 

FFPsyContext psy;

struct FFPsyPreprocessContext* psypp;

const AACCoefficientsEncoder *coder;

int cur_channel; 

int random_state;

float lambda;

int last_frame_pb_count; 

float lambda_sum; 

int lambda_count; 

enum RawDataBlockType cur_type; 

AudioFrameQueue afq;

DECLARE_ALIGNED(16, int, qcoefs)[96]; 

DECLARE_ALIGNED(32, float, scoefs)[1024]; 

uint16_t quantize_band_cost_cache_generation;

AACQuantizeBandCostCacheEntry quantize_band_cost_cache[256][128]; 

void (*abs_pow34)(float *out, const float *in, const int size);

void (*quant_bands)(int *out, const float *in, const float *scaled,

int size, int is_signed, int maxval, const float Q34,

const float rounding);

struct {

float *samples;

} buffer;

} AACEncContext;
