mc_type2 = ((mv_y2 & 1) << 1) | (mv_x2 & 1);

mv_x >>= 1;

mv_y >>= 1;

mv_x2 >>= 1;

mv_y2 >>= 1; 

}

if (mb->type == 2)

mc_type = -1;

if (mb->type != 2 && mb->type != 3)

mc_type2 = -1;

if (mb->type) {

