function.block:
{

static const uintptr_t size = sizeof(muxer_list)/sizeof(muxer_list[0]) - 1;

uintptr_t i = (uintptr_t)*opaque;

const AVOutputFormat *f = NULL;

if (i < size) {

f = muxer_list[i];

} else if (indev_list) {

f = outdev_list[i - size];

}

if (f)

*opaque = (void*)(i + 1);

return f;

}

{

static const uintptr_t size = sizeof(demuxer_list)/sizeof(demuxer_list[0]) - 1;

uintptr_t i = (uintptr_t)*opaque;

const AVInputFormat *f = NULL;

if (i < size) {

f = demuxer_list[i];

} else if (outdev_list) {

f = indev_list[i - size];

}

if (f)

*opaque = (void*)(i + 1);

return f;

}

{

AVOutputFormat *prevout = NULL, *out;

AVInputFormat *previn = NULL, *in;

ff_mutex_lock(&avpriv_register_devices_mutex);

for (int i = 0; (out = (AVOutputFormat*)muxer_list[i]); i++) {

if (prevout)

prevout->next = out;

prevout = out;

}

if (outdev_list) {

for (int i = 0; (out = (AVOutputFormat*)outdev_list[i]); i++) {

if (prevout)

prevout->next = out;

prevout = out;

}

}

for (int i = 0; (in = (AVInputFormat*)demuxer_list[i]); i++) {

if (previn)

previn->next = in;

previn = in;

}

if (indev_list) {

for (int i = 0; (in = (AVInputFormat*)indev_list[i]); i++) {

if (previn)

previn->next = in;

previn = in;

}

}

ff_mutex_unlock(&avpriv_register_devices_mutex);

}

{

ff_thread_once(&av_format_next_init, av_format_init_next);

if (f)

#if FF_API_AVIOFORMAT

return f->next;

#else

return (AVInputFormat *) f->next;

#endif

else {

void *opaque = NULL;

return (AVInputFormat *)av_demuxer_iterate(&opaque);

}

}

{

ff_thread_once(&av_format_next_init, av_format_init_next);

if (f)

#if FF_API_AVIOFORMAT

return f->next;

#else

return (AVOutputFormat *) f->next;

#endif

else {

void *opaque = NULL;

return (AVOutputFormat *)av_muxer_iterate(&opaque);

}

}

{

ff_mutex_lock(&avpriv_register_devices_mutex);

outdev_list = o;

indev_list = i;

ff_mutex_unlock(&avpriv_register_devices_mutex);

#if FF_API_NEXT

av_format_init_next();

#endif

}
