unit.decl_stmt:
const struct ltc_cipher_descriptor rc5_desc =

{

"rc5",

2,

8, 128, 8, 12,

&rc5_setup,

&rc5_ecb_encrypt,

&rc5_ecb_decrypt,

&rc5_test,

&rc5_done,

&rc5_keysize,

NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL

};

decl.block:
{

ulong32 L[64], *S, A, B, i, j, v, s, t, l;

LTC_ARGCHK(skey != NULL);

LTC_ARGCHK(key != NULL);

if (num_rounds == 0) {

num_rounds = rc5_desc.default_rounds;

}

if (num_rounds < 12 || num_rounds > 24) {

return CRYPT_INVALID_ROUNDS;

}

if (keylen < 8 || keylen > 128) {

return CRYPT_INVALID_KEYSIZE;

}

skey->rc5.rounds = num_rounds;

S = skey->rc5.K;

for (A = i = j = 0; i < (ulong32)keylen; ) {

A = (A << 8) | ((ulong32)(key[i++] & 255));

if ((i & 3) == 0) {

L[j++] = BSWAP(A);

A = 0;

}

}

if ((keylen & 3) != 0) {

A <<= (ulong32)((8 * (4 - (keylen&3))));

L[j++] = BSWAP(A);

}

t = (ulong32)(2 * (num_rounds + 1));

XMEMCPY(S, stab, t * sizeof(*S));

s = 3 * MAX(t, j);

l = j;

for (A = B = i = j = v = 0; v < s; v++) {

A = S[i] = ROLc(S[i] + A + B, 3);

B = L[j] = ROL(L[j] + A + B, (A+B));

if (++i == t) { i = 0; }

if (++j == l) { j = 0; }

}

return CRYPT_OK;

}

function.block:
{

int x;

x = _rc5_setup(key, keylen, num_rounds, skey);

burn_stack(sizeof(ulong32) * 122 + sizeof(int));

return x;

}

decl.block:
{

ulong32 A, B;

const ulong32 *K;

int r;

LTC_ARGCHK(skey != NULL);

LTC_ARGCHK(pt != NULL);

LTC_ARGCHK(ct != NULL);

if (skey->rc5.rounds < 12 || skey->rc5.rounds > 24) {

return CRYPT_INVALID_ROUNDS;

}

LOAD32L(A, &pt[0]);

LOAD32L(B, &pt[4]);

A += skey->rc5.K[0];

B += skey->rc5.K[1];

K = skey->rc5.K + 2;

if ((skey->rc5.rounds & 1) == 0) {

for (r = 0; r < skey->rc5.rounds; r += 2) {

A = ROL(A ^ B, B) + K[0];

B = ROL(B ^ A, A) + K[1];

A = ROL(A ^ B, B) + K[2];

B = ROL(B ^ A, A) + K[3];

K += 4;

}

} else {

for (r = 0; r < skey->rc5.rounds; r++) {

A = ROL(A ^ B, B) + K[0];

B = ROL(B ^ A, A) + K[1];

K += 2;

}

}

STORE32L(A, &ct[0]);

STORE32L(B, &ct[4]);

return CRYPT_OK;

}

function.block:
{

int err = _rc5_ecb_encrypt(pt, ct, skey);

burn_stack(sizeof(ulong32) * 2 + sizeof(int));

return err;

}

decl.block:
{

ulong32 A, B;

const ulong32 *K;

int r;

LTC_ARGCHK(skey != NULL);

LTC_ARGCHK(pt != NULL);

LTC_ARGCHK(ct != NULL);

if (skey->rc5.rounds < 12 || skey->rc5.rounds > 24) {

return CRYPT_INVALID_ROUNDS;

}

LOAD32L(A, &ct[0]);

LOAD32L(B, &ct[4]);

K = skey->rc5.K + (skey->rc5.rounds << 1);

if ((skey->rc5.rounds & 1) == 0) {

K -= 2;

for (r = skey->rc5.rounds - 1; r >= 0; r -= 2) {

B = ROR(B - K[3], A) ^ A;

A = ROR(A - K[2], B) ^ B;

B = ROR(B - K[1], A) ^ A;

A = ROR(A - K[0], B) ^ B;

K -= 4;

}

} else {

for (r = skey->rc5.rounds - 1; r >= 0; r--) {

B = ROR(B - K[1], A) ^ A;

A = ROR(A - K[0], B) ^ B;

K -= 2;

}

}

A -= skey->rc5.K[0];

B -= skey->rc5.K[1];

STORE32L(A, &pt[0]);

STORE32L(B, &pt[4]);

return CRYPT_OK;

}

function.block:
{

int err = _rc5_ecb_decrypt(ct, pt, skey);

burn_stack(sizeof(ulong32) * 2 + sizeof(int));

return err;

}

{

#if !defined(LTC_TEST)
return CRYPT_NOP;

#else

static const struct {

unsigned char key[16], pt[8], ct[8];

} tests[] = {

{

{ 0x91, 0x5f, 0x46, 0x19, 0xbe, 0x41, 0xb2, 0x51,

0x63, 0x55, 0xa5, 0x01, 0x10, 0xa9, 0xce, 0x91 },

{ 0x21, 0xa5, 0xdb, 0xee, 0x15, 0x4b, 0x8f, 0x6d },

{ 0xf7, 0xc0, 0x13, 0xac, 0x5b, 0x2b, 0x89, 0x52 }

},

{

{ 0x78, 0x33, 0x48, 0xe7, 0x5a, 0xeb, 0x0f, 0x2f,

0xd7, 0xb1, 0x69, 0xbb, 0x8d, 0xc1, 0x67, 0x87 },

{ 0xF7, 0xC0, 0x13, 0xAC, 0x5B, 0x2B, 0x89, 0x52 },

{ 0x2F, 0x42, 0xB3, 0xB7, 0x03, 0x69, 0xFC, 0x92 }

},

{

{ 0xDC, 0x49, 0xdb, 0x13, 0x75, 0xa5, 0x58, 0x4f,

0x64, 0x85, 0xb4, 0x13, 0xb5, 0xf1, 0x2b, 0xaf },

{ 0x2F, 0x42, 0xB3, 0xB7, 0x03, 0x69, 0xFC, 0x92 },

{ 0x65, 0xc1, 0x78, 0xb2, 0x84, 0xd1, 0x97, 0xcc }

}

};

unsigned char tmp[2][8];

int x, y, err;

symmetric_key key;

for (x = 0; x < (int)(sizeof(tests) / sizeof(tests[0])); x++) {

if ((err = rc5_setup(tests[x].key, 16, 12, &key)) != CRYPT_OK) {

return err;

}

rc5_ecb_encrypt(tests[x].pt, tmp[0], &key);

rc5_ecb_decrypt(tmp[0], tmp[1], &key);

if (compare_testvector(tmp[0], 8, tests[x].ct, 8, "RC5 Encrypt", x) != 0 ||

compare_testvector(tmp[1], 8, tests[x].pt, 8, "RC5 Decrypt", x) != 0) {

return CRYPT_FAIL_TESTVECTOR;

}

for (y = 0; y < 8; y++) tmp[0][y] = 0;

for (y = 0; y < 1000; y++) rc5_ecb_encrypt(tmp[0], tmp[0], &key);

for (y = 0; y < 1000; y++) rc5_ecb_decrypt(tmp[0], tmp[0], &key);

for (y = 0; y < 8; y++) if (tmp[0][y] != 0) return CRYPT_FAIL_TESTVECTOR;

}

return CRYPT_OK;

#endif

}

{

LTC_ARGCHK(keysize != NULL);

if (*keysize < 8) {

return CRYPT_INVALID_KEYSIZE;

}

if (*keysize > 128) {

*keysize = 128;

}

return CRYPT_OK;

}
