function.block:
{
int len;
int i;
int m;

len = utf8len_tab[*p];
if (len == 1)
return 1; 
if (len > size)
m = size; 
else
m = len;
for (i = 1; i < m; ++i)
if ((p[i] & 0xc0) != 0x80)
return 1;
return len;
}

{
int len;
int prevlen;

if (size < 1 || *p == NUL)
return 0;
if (p[0] < 0x80 && (size == 1 || p[1] < 0x80)) 
return 1;


len = utf_ptr2len_len(p, size);


if ((len == 1 && p[0] >= 0x80) || len > size)
return 1;





prevlen = 0;
while (len < size) {
int len_next_char;

if (p[len] < 0x80)
break;





len_next_char = utf_ptr2len_len(p + len, size - len);
if (len_next_char > size - len)
break;

if (!UTF_COMPOSINGLIKE(p + prevlen, p + len))
break;


prevlen = len;
len += len_next_char;
}
return len;
}

{
if (c < 0x80) {
return 1;
} else if (c < 0x800) {
return 2;
} else if (c < 0x10000) {
return 3;
} else if (c < 0x200000) {
return 4;
} else if (c < 0x4000000) {
return 5;
} else {
return 6;
}
}

{
if (c < 0x80) { 
buf[0] = c;
return 1;
} else if (c < 0x800) { 
buf[0] = 0xc0 + ((unsigned)c >> 6);
buf[1] = 0x80 + (c & 0x3f);
return 2;
} else if (c < 0x10000) { 
buf[0] = 0xe0 + ((unsigned)c >> 12);
buf[1] = 0x80 + (((unsigned)c >> 6) & 0x3f);
buf[2] = 0x80 + (c & 0x3f);
return 3;
} else if (c < 0x200000) { 
buf[0] = 0xf0 + ((unsigned)c >> 18);
buf[1] = 0x80 + (((unsigned)c >> 12) & 0x3f);
buf[2] = 0x80 + (((unsigned)c >> 6) & 0x3f);
buf[3] = 0x80 + (c & 0x3f);
return 4;
} else if (c < 0x4000000) { 
buf[0] = 0xf8 + ((unsigned)c >> 24);
buf[1] = 0x80 + (((unsigned)c >> 18) & 0x3f);
buf[2] = 0x80 + (((unsigned)c >> 12) & 0x3f);
buf[3] = 0x80 + (((unsigned)c >> 6) & 0x3f);
buf[4] = 0x80 + (c & 0x3f);
return 5;
} else { 
buf[0] = 0xfc + ((unsigned)c >> 30);
buf[1] = 0x80 + (((unsigned)c >> 24) & 0x3f);
buf[2] = 0x80 + (((unsigned)c >> 18) & 0x3f);
buf[3] = 0x80 + (((unsigned)c >> 12) & 0x3f);
buf[4] = 0x80 + (((unsigned)c >> 6) & 0x3f);
buf[5] = 0x80 + (c & 0x3f);
return 6;
}
}

{
if (*p == NUL) {
return 0;
}
const int len = utf8len_tab[*p];
for (int i = 1; i < len; i++) {
if ((p[i] & 0xc0) != 0x80) {
return 1;
}
}
return len;
}

{
uint8_t b0 = (uint8_t)(*p);

if (b0 == NUL) {
return 0;
}
if (b0 < 0x80 && p[1] < 0x80) { 
return 1;
}


int len = utf_ptr2len(p);


if (len == 1 && b0 >= 0x80) {
return 1;
}



int prevlen = 0;
for (;;) {
if (p[len] < 0x80 || !UTF_COMPOSINGLIKE(p + prevlen, p + len)) {
return len;
}


prevlen = len;
len += utf_ptr2len(p + len);
}
}

{
const size_t l = utfc_ptr2len(*fp);

memmove(*tp, *fp, (size_t)l);
*tp += l;
*fp += l;
}
