function.block:
{
int i;
r_strbuf_init (buf);
r_strbuf_append (buf, "{");
cs_riscv *x = &insn->detail->riscv;
r_strbuf_append (buf, "\"operands\":[");
for (i = 0; i < x->op_count; i++) {
cs_riscv_op *op = &x->operands[i];
if (i > 0) {
r_strbuf_append (buf, ",");
}
r_strbuf_append (buf, "{");
switch (op->type) {
case RISCV_OP_REG:
r_strbuf_append (buf, "\"type\":\"reg\"");
r_strbuf_appendf (buf, ",\"value\":\"%s\"", cs_reg_name (handle, op->reg));
break;
case RISCV_OP_IMM:
r_strbuf_append (buf, "\"type\":\"imm\"");
r_strbuf_appendf (buf, ",\"value\":%"PFMT64d, op->imm);
break;
case RISCV_OP_MEM:
r_strbuf_append (buf, "\"type\":\"mem\"");
if (op->mem.base != RISCV_REG_INVALID) {
r_strbuf_appendf (buf, ",\"base\":\"%s\"", cs_reg_name (handle, op->mem.base));
}
r_strbuf_appendf (buf, ",\"disp\":%"PFMT64d"", op->mem.disp);
break;
default:
r_strbuf_append (buf, "\"type\":\"invalid\"");
break;
}
r_strbuf_append (buf, "}");
}
r_strbuf_append (buf, "]");
r_strbuf_append (buf, "}");
}

{
*buf = 0;
switch (insn->detail->riscv.operands[n].type) {
case RISCV_OP_INVALID:
break;
case RISCV_OP_REG:
sprintf (buf, "%s",
cs_reg_name (*handle,
insn->detail->riscv.operands[n].reg));
break;
case RISCV_OP_IMM:
{
st64 x = (st64)insn->detail->riscv.operands[n].imm;
sprintf (buf, "%"PFMT64d, x);
}
break;
case RISCV_OP_MEM:
{
int disp = insn->detail->riscv.operands[n].mem.disp;
if (disp<0) {
sprintf (buf, "%"PFMT64d",%s,-",
(ut64)-insn->detail->riscv.operands[n].mem.disp,
cs_reg_name (*handle,
insn->detail->riscv.operands[n].mem.base));
} else {
sprintf (buf, "0x%"PFMT64x",%s,+",
(ut64)insn->detail->riscv.operands[n].mem.disp,
cs_reg_name (*handle,
insn->detail->riscv.operands[n].mem.base));
}
}
break;
}
return buf;
}

{
char str[8][32] = {{0}};
int i;

r_strbuf_init (&op->esil);
r_strbuf_set (&op->esil, "");

if (insn) {

for (i = 0; i < insn->detail->riscv.op_count && i < 8; i++) {
*str[i] = 0;
ARG (i);
}
}

switch (insn->id) {



}
return 0;
}

{
if (!reg) {
return -1;
}
switch (OPERAND (reg_num).type) {
case RISCV_OP_REG:
reg->name = (char *)cs_reg_name (handle, OPERAND (reg_num).reg);
break;
case RISCV_OP_MEM:
if (OPERAND (reg_num).mem.base != RISCV_REG_INVALID) {
reg->name = (char *)cs_reg_name (handle, OPERAND (reg_num).mem.base);
}
default:
break;
}
return 0;
}

{
static RRegItem reg;
switch (op->type & R_ANAL_OP_TYPE_MASK) {
case R_ANAL_OP_TYPE_LOAD:
if (OPERAND(1).type == RISCV_OP_MEM) {
ZERO_FILL (reg);
op->src[0] = r_anal_value_new ();
op->src[0]->reg = &reg;
parse_reg_name (op->src[0]->reg, *handle, insn, 1);
op->src[0]->delta = OPERAND(1).mem.disp;
}
break;
case R_ANAL_OP_TYPE_STORE:
if (OPERAND(1).type == RISCV_OP_MEM) {
ZERO_FILL (reg);
op->dst = r_anal_value_new ();
op->dst->reg = &reg;
parse_reg_name (op->dst->reg, *handle, insn, 1);
op->dst->delta = OPERAND(1).mem.disp;
}
break;
case R_ANAL_OP_TYPE_SHL:
case R_ANAL_OP_TYPE_SHR:
case R_ANAL_OP_TYPE_SAR:
case R_ANAL_OP_TYPE_XOR:
case R_ANAL_OP_TYPE_SUB:
case R_ANAL_OP_TYPE_AND:
case R_ANAL_OP_TYPE_ADD:
case R_ANAL_OP_TYPE_OR:
SET_SRC_DST_3_REG_OR_IMM (op);
break;
case R_ANAL_OP_TYPE_MOV:
SET_SRC_DST_3_REG_OR_IMM (op);
break;
case R_ANAL_OP_TYPE_DIV: 
#if 0
capstone bug
------------
$ r2 -a riscv -e cfg.bigendian=1 -c "wx 0083001b" -

[0x00000000]> aoj~{}
[
{
"opcode": "divu zero, a0, v1",
"disasm": "divu zero, a0, v1",
"mnemonic": "divu",
"sign": false,
"prefix": 0,
"id": 192,
"opex": {
"operands": [
{
"type": "reg",
"value": "a0"
},
{
"type": "reg",
"value": "v1"
}
]
},
#endif
if (OPERAND(0).type == RISCV_OP_REG && OPERAND(1).type == RISCV_OP_REG && OPERAND(2).type == RISCV_OP_REG) {
SET_SRC_DST_3_REGS (op);
} else if (OPERAND(0).type == RISCV_OP_REG && OPERAND(1).type == RISCV_OP_REG) {
SET_SRC_DST_2_REGS (op);
} else {
eprintf ("Unknown div at 0x%08"PFMT64x"\n", op->addr);
}
break;
}
if (insn && (insn->id == RISCV_INS_SLTI || insn->id == RISCV_INS_SLTIU)) {
SET_SRC_DST_3_IMM (op);
}
}

{
switch (op->type & R_ANAL_OP_TYPE_MASK) {
case R_ANAL_OP_TYPE_LOAD:
op->direction = R_ANAL_OP_DIR_READ;
break;
case R_ANAL_OP_TYPE_STORE:
op->direction = R_ANAL_OP_DIR_WRITE;
break;
case R_ANAL_OP_TYPE_LEA:
op->direction = R_ANAL_OP_DIR_REF;
break;
case R_ANAL_OP_TYPE_CALL:
case R_ANAL_OP_TYPE_JMP:
case R_ANAL_OP_TYPE_UJMP:
case R_ANAL_OP_TYPE_UCALL:
op->direction = R_ANAL_OP_DIR_EXEC;
break;
default:
break;
}
}

{
int n, ret, opsize = -1;
static csh hndl = 0;
static int omode = -1;
static int obits = 32;
cs_insn* insn;
int mode = (anal->bits==64)? CS_MODE_RISCV64: CS_MODE_RISCV32;
if (mode != omode || anal->bits != obits) {
cs_close (&hndl);
hndl = 0;
omode = mode;
obits = anal->bits;
}

op->delay = 0;
op->type = R_ANAL_OP_TYPE_ILL;
op->addr = addr;
if (len < 4) {
return -1;
}
op->size = 4;
if (hndl == 0) {
ret = cs_open (CS_ARCH_RISCV, mode, &hndl);
if (ret != CS_ERR_OK) {
goto fin;
}
cs_option (hndl, CS_OPT_DETAIL, CS_OPT_ON);
}
n = cs_disasm (hndl, (ut8*)buf, len, addr, 1, &insn);
if (n < 1 || insn->size < 1) {
goto beach;
}
op->type = R_ANAL_OP_TYPE_NULL;
op->delay = 0;
op->jump = UT64_MAX;
op->fail = UT64_MAX;
op->id = insn->id;
opsize = op->size = insn->size;
switch (insn->id) {
case RISCV_INS_C_NOP:
op->type = R_ANAL_OP_TYPE_NOP;
break;
case RISCV_INS_INVALID:
op->type = R_ANAL_OP_TYPE_ILL;
break;
case RISCV_INS_C_JALR:
op->type = R_ANAL_OP_TYPE_UCALL;
break;
case RISCV_INS_C_JR:
op->type = R_ANAL_OP_TYPE_UJMP;
break;
case RISCV_INS_C_MV:
op->type = R_ANAL_OP_TYPE_MOV;
break;
case RISCV_INS_JAL:
op->type = R_ANAL_OP_TYPE_CALL;
op->jump = IMM(0);
op->fail = op->addr + op->size;
break;
case RISCV_INS_MRET:
case RISCV_INS_SRET:
case RISCV_INS_URET:
op->type = R_ANAL_OP_TYPE_RET;
break;
}
beach:
set_opdir (op);
if (insn && mask & R_ANAL_OP_MASK_OPEX) {
opex (&op->opex, hndl, insn);
}
if (mask & R_ANAL_OP_MASK_ESIL) {
if (analop_esil (anal, op, addr, buf, len, &hndl, insn) != 0) {
r_strbuf_fini (&op->esil);
}
}
if (mask & R_ANAL_OP_MASK_VAL) {
op_fillval (anal, op, &hndl, insn);
}
cs_free (insn, n);

fin:
return opsize;
}

{
switch (q) {
case R_ANAL_ARCHINFO_ALIGN:
return 4;
case R_ANAL_ARCHINFO_MAX_OP_SIZE:
return 4;
case R_ANAL_ARCHINFO_MIN_OP_SIZE:
if (anal->bits == 64) {
return 4;
}
return 2;
}
return 0;
}
