function.block:
{
if (idx < 0 || idx >= lastUtfBlock) {
return NULL;
}
return r_utf_blocks[idx].name;
}

{
if (ptrlen < 1) {
return 0;
}
if (ptrlen > 0 && ptr[0] < 0x80) {
if (ch) {
*ch = (ut32)ptr[0];
}
return 1;
} else if (ptrlen>1 && (ptr[0]&0xe0) == 0xc0 && (ptr[1]&0xc0) == 0x80) {
if (ch) {
*ch = (ptr[0] & 0x1f) << 6 | (ptr[1] & 0x3f);
}
return 2;
} else if (ptrlen>2 && (ptr[0]&0xf0) == 0xe0 && (ptr[1]&0xc0) == 0x80 && (ptr[2]&0xc0) == 0x80) {
if (ch) {
*ch = (ptr[0] & 0xf) << 12 | (ptr[1] & 0x3f) << 6 | (ptr[2] & 0x3f);
}
return 3;
} else if (ptrlen>3 && (ptr[0]&0xf8) == 0xf0 && (ptr[1]&0xc0) == 0x80 && (ptr[2]&0xc0) == 0x80 && (ptr[3]&0xc0) == 0x80) {
if (ch) {
*ch = (ptr[0] & 7) << 18 | (ptr[1] & 0x3f) << 12 | (ptr[2] & 0x3f) << 6 | (ptr[3] & 0x3f);
}
return 4;
}
return 0;
}

{
if (ch < 0x80) {
ptr[0] = (ut8)ch;
return 1;
}
else if (ch < 0x800) {
ptr[0] = 0xc0 | (ch >> 6);
ptr[1] = 0x80 | (ch & 0x3f);
return 2;
}
else if (ch < 0x10000) {
ptr[0] = 0xe0 | (ch >> 12);
ptr[1] = 0x80 | ((ch >> 6) & 0x3f);
ptr[2] = 0x80 | (ch & 0x3f);
return 3;
}
else if (ch < 0x200000) {
ptr[0] = 0xf0 | (ch >> 18);
ptr[1] = 0x80 | ((ch >> 12) & 0x3f);
ptr[2] = 0x80 | ((ch >> 6) & 0x3f);
ptr[3] = 0x80 | (ch & 0x3f );
return 4;
}
return 0;
}

{
int i, pos = 0;

if (!str || !dst) {
return -1;
}

for (i = 0; i < sizeof (str) - 1 && str[i] && pos < dst_length - 1; i++) {
pos += r_utf8_encode (&dst[pos], str[i]);
}

dst[pos++] = '\0';
return pos;
}

{
int i, len = 0;

for (i = 0; str[i]; i++) {
if ((str[i] & 0xc0) != 0x80) {
len++;
}
}

return len;
}

{

if (c < 0x34F) {





return !( c <= 0x1F || ( c >= 0x7F && c <= 0x9F));
}

const int last = nonprintable_ranges_count;

int low = 0;
int hi = last - 1;

do {
int mid = (low + hi) >> 1;
if (c >= nonprintable_ranges[mid].from && c <= nonprintable_ranges[mid].to) {
return false;
}
if (mid < last && c > nonprintable_ranges[mid].to) {
low = mid + 1;
}
if (mid < last && c < nonprintable_ranges[mid].from) {
hi = mid - 1;
}
} while (low <= hi);

return true;
}

{
if (!wc || !len || len < -1) {
return NULL;
}
char *rutf8 = NULL;
int csize;

if ((csize = WideCharToMultiByte (CP_UTF8, 0, wc, len, NULL, 0, NULL, NULL))) {
++csize;
if ((rutf8 = malloc (csize))) {
WideCharToMultiByte (CP_UTF8, 0, wc, len, rutf8, csize, NULL, NULL);
if (len != -1) {
rutf8[csize - 1] = '\0';
}
}
}
return rutf8;
}

{
if (!cstring || !len || len < -1) {
return NULL;
}
wchar_t *rutf16 = NULL;
int wcsize;

if ((wcsize = MultiByteToWideChar (CP_UTF8, 0, cstring, len, NULL, 0))) {
++wcsize;
if ((rutf16 = (wchar_t *) calloc (wcsize, sizeof (wchar_t)))) {
MultiByteToWideChar (CP_UTF8, 0, cstring, len, rutf16, wcsize);
if (len != -1) {
rutf16[wcsize - 1] = L'\0';
}
}
}
return rutf16;
}

{
if (!str || !len || len < -1) {
return NULL;
}
char *acp = NULL;
int wcsize, csize;
if ((wcsize = MultiByteToWideChar (CP_UTF8, 0, str, len, NULL, 0))) {
wchar_t *rutf16;
++wcsize;
if ((rutf16 = (wchar_t *)calloc (wcsize, sizeof (wchar_t)))) {
MultiByteToWideChar (CP_UTF8, 0, str, len, rutf16, wcsize);
if (len != -1) {
rutf16[wcsize - 1] = L'\0';
}
if ((csize = WideCharToMultiByte (CP_ACP, 0, rutf16, wcsize, NULL, 0, NULL, NULL))) {
++csize;
if ((acp = malloc (csize))) {
WideCharToMultiByte (CP_ACP, 0, rutf16, wcsize, acp, csize, NULL, NULL);
if (len != -1) {
acp[csize - 1] = '\0';
}
}
}
free (rutf16);
}
}
return acp;
}

{
if (!str || !len || len < -1) {
return NULL;
}
int wcsize;
if ((wcsize = MultiByteToWideChar (CP_ACP, 0, str, len, NULL, 0))) {
wchar_t *rutf16;
++wcsize;
if ((rutf16 = (wchar_t *) calloc (wcsize, sizeof (wchar_t)))) {
MultiByteToWideChar (CP_ACP, 0, str, len, rutf16, wcsize);
if (len != -1) {
rutf16[wcsize - 1] = L'\0';
}
char *ret = r_utf16_to_utf8_l (rutf16, wcsize);
free (rutf16);
return ret;
}
}
return NULL;
}

{
const int last = r_utf_blocks_count;
int low, hi, mid;

low = 0;
hi = last - 1;

do {
mid = (low + hi) >> 1;
if (ch >= r_utf_blocks[mid].from && ch <= r_utf_blocks[mid].to) {
return mid;
}
if (mid < last && ch > r_utf_blocks[mid].to) {
low = mid + 1;
}
if (mid < last && ch < r_utf_blocks[mid].from) {
hi = mid - 1;
}
} while (low <= hi);

return r_utf_blocks_count - 1; 
}

{
if (!str) {
return NULL;
}
if (len < 0) {
len = strlen ((const char *)str);
}
static int block_freq[r_utf_blocks_count] = {0};
int *list = R_NEWS (int, len + 1);
if (!list) {
return NULL;
}
int *freq_list_ptr = NULL;
if (freq_list) {
*freq_list = R_NEWS (int, len + 1);
if (!*freq_list) {
free (list);
return NULL;
}
freq_list_ptr = *freq_list;
}
int *list_ptr = list;
const ut8 *str_ptr = str;
const ut8 *str_end = str + len;
RRune ch;
while (str_ptr < str_end) {
int block_idx;
int ch_bytes = r_utf8_decode (str_ptr, str_end - str_ptr, &ch);
if (!ch_bytes) {
block_idx = r_utf_blocks_count - 1;
ch_bytes = 1;
} else {
block_idx = r_utf_block_idx (ch);
}
if (!block_freq[block_idx]) {
*list_ptr = block_idx;
list_ptr++;
}
block_freq[block_idx]++;
str_ptr += ch_bytes;
}
*list_ptr = -1;
if (freq_list_ptr) {
for (list_ptr = list; *list_ptr != -1; list_ptr++) {
*freq_list_ptr = block_freq[*list_ptr];
freq_list_ptr++;
}
*freq_list_ptr = -1;
}
for (list_ptr = list; *list_ptr != -1; list_ptr++) {
block_freq[*list_ptr] = 0;
}
return list;
}

{
if (ptrlen > 3) {
if (ptr[0] == 0xff && ptr[1] == 0xfe && !ptr[2] && !ptr[3]) {
return R_STRING_ENC_UTF32LE;
}
if (!ptr[0] && !ptr[1] && ptr[2] == 0xfe && ptr[3] == 0xff) {
return R_STRING_ENC_UTF32BE;
}
}
if (ptrlen > 2) {
if (ptr[0] == 0xef && ptr[1] == 0xbb && ptr[2] == 0xbf) {
return R_STRING_ENC_UTF8;
}
}
if (ptrlen > 1) {
if (ptr[0] == 0xff && ptr[1] == 0xfe) {
return R_STRING_ENC_UTF16LE;
}
if (ptr[0] == 0xfe && ptr[1] == 0xff) {
return R_STRING_ENC_UTF16BE;
}
}
return R_STRING_ENC_GUESS;
}
