unit.typedef:
typedef struct digraph

{

char_u char1;

char_u char2;

result_T result;

} digr_T;

function.block:
{

static int backspaced; 

static int lastchar; 

if (c == -1) 

{

backspaced = -1;

}

else if (p_dg)

{

if (backspaced >= 0)

c = getdigraph(backspaced, c, FALSE);

backspaced = -1;

if ((c == K_BS || c == Ctrl_H) && lastchar >= 0)

backspaced = lastchar;

}

lastchar = c;

return c;

}

{

int val = val_arg;

int i;

int use_defaults;

digr_T *dp;

static char_u r[3];

#if defined(USE_UNICODE_DIGRAPHS)

if (!enc_utf8)

{

char_u buf[6], *to;

vimconv_T vc;

i = mb_char2bytes(val, buf);

vc.vc_type = CONV_NONE;

if (convert_setup(&vc, p_enc, (char_u *)"utf-8") == OK)

{

vc.vc_fail = TRUE;

to = string_convert(&vc, buf, &i);

if (to != NULL)

{

val = utf_ptr2char(to);

vim_free(to);

}

(void)convert_setup(&vc, NULL, NULL);

}

}

#endif

for (use_defaults = 0; use_defaults <= 1; use_defaults++)

{

if (use_defaults == 0)

dp = (digr_T *)user_digraphs.ga_data;

else

dp = digraphdefault;

for (i = 0; use_defaults ? dp->char1 != NUL

: i < user_digraphs.ga_len; ++i)

{

if (dp->result == val)

{

r[0] = dp->char1;

r[1] = dp->char2;

r[2] = NUL;

return r;

}

++dp;

}

}

return NULL;

}

{

int c, cc;

++no_mapping;

++allow_keys;

c = plain_vgetc();

--no_mapping;

--allow_keys;

if (c != ESC) 

{

if (IS_SPECIAL(c)) 

return c;

if (cmdline)

{

if (char2cells(c) == 1

#if defined(FEAT_CRYPT) || defined(FEAT_EVAL)

&& cmdline_star == 0

#endif

)

putcmdline(c, TRUE);

}

#if defined(FEAT_CMDL_INFO)
else

add_to_showcmd(c);

#endif

++no_mapping;

++allow_keys;

cc = plain_vgetc();

--no_mapping;

--allow_keys;

if (cc != ESC) 

return getdigraph(c, cc, TRUE);

}

return NUL;

}

{

int i;

int retval = 0;

digr_T *dp;

if (IS_SPECIAL(char1) || IS_SPECIAL(char2))

return char2;

dp = (digr_T *)user_digraphs.ga_data;

for (i = 0; i < user_digraphs.ga_len; ++i)

{

if ((int)dp->char1 == char1 && (int)dp->char2 == char2)

{

retval = dp->result;

break;

}

++dp;

}

if (retval == 0)

{

dp = digraphdefault;

for (i = 0; dp->char1 != 0; ++i)

{

if ((int)dp->char1 == char1 && (int)dp->char2 == char2)

{

retval = dp->result;

break;

}

++dp;

}

}

#if defined(USE_UNICODE_DIGRAPHS)
if (retval != 0 && !enc_utf8)

{

char_u buf[6], *to;

vimconv_T vc;

i = utf_char2bytes(retval, buf);

retval = 0;

vc.vc_type = CONV_NONE;

if (convert_setup(&vc, (char_u *)"utf-8", p_enc) == OK)

{

vc.vc_fail = TRUE;

to = string_convert(&vc, buf, &i);

if (to != NULL)

{

retval = (*mb_ptr2char)(to);

vim_free(to);

}

(void)convert_setup(&vc, NULL, NULL);

}

}

#endif

if (!has_mbyte && retval > 0xff)

retval = 0;

if (retval == 0) 

{

if (char1 == ' ' && meta_char) 

return (char2 | 0x80);

return char2;

}

return retval;

}

{

int retval;

if (((retval = getexactdigraph(char1, char2, meta_char)) == char2)

&& (char1 != char2)

&& ((retval = getexactdigraph(char2, char1, meta_char)) == char1))

return char2;

return retval;

}

{

int char1, char2, n;

int i;

digr_T *dp;

while (*str != NUL)

{

str = skipwhite(str);

if (*str == NUL)

return;

char1 = *str++;

char2 = *str++;

if (char2 == 0)

{

emsg(_(e_invarg));

return;

}

if (char1 == ESC || char2 == ESC)

{

emsg(_("E104: Escape not allowed in digraph"));

return;

}

str = skipwhite(str);

if (!VIM_ISDIGIT(*str))

{

emsg(_(e_number_exp));

return;

}

n = getdigits(&str);

dp = (digr_T *)user_digraphs.ga_data;

for (i = 0; i < user_digraphs.ga_len; ++i)

{

if ((int)dp->char1 == char1 && (int)dp->char2 == char2)

{

dp->result = n;

break;

}

++dp;

}

if (i == user_digraphs.ga_len)

{

if (ga_grow(&user_digraphs, 1) == OK)

{

dp = (digr_T *)user_digraphs.ga_data + user_digraphs.ga_len;

dp->char1 = char1;

dp->char2 = char2;

dp->result = n;

++user_digraphs.ga_len;

}

}

}

}

{

int i;

digr_T *dp;

result_T previous = 0;

msg_putchar('\n');

dp = digraphdefault;

for (i = 0; dp->char1 != NUL && !got_int; ++i)

{

#if defined(USE_UNICODE_DIGRAPHS)

digr_T tmp;

tmp.char1 = dp->char1;

tmp.char2 = dp->char2;

tmp.result = getexactdigraph(tmp.char1, tmp.char2, FALSE);

if (tmp.result != 0 && tmp.result != tmp.char2

&& (has_mbyte || tmp.result <= 255))

printdigraph(&tmp, use_headers ? &previous : NULL);

#else

if (getexactdigraph(dp->char1, dp->char2, FALSE) == dp->result

&& (has_mbyte || dp->result <= 255))

printdigraph(dp, use_headers ? &previous : NULL);

#endif

++dp;

ui_breakcheck();

}

dp = (digr_T *)user_digraphs.ga_data;

for (i = 0; i < user_digraphs.ga_len && !got_int; ++i)

{

#if defined(USE_UNICODE_DIGRAPHS)

if (previous >= 0 && use_headers)

digraph_header(_("Custom"));

previous = -1;

#endif

printdigraph(dp, NULL);

ui_breakcheck();

++dp;

}

must_redraw = CLEAR; 

}

{

char_u buf[30];

char_u *p;

int list_width;

if ((dy_flags & DY_UHEX) || has_mbyte)

list_width = 13;

else

list_width = 11;

if (dp->result != 0)

{

#if defined(USE_UNICODE_DIGRAPHS)

if (previous != NULL)

{

int i;

for (i = 0; header_table[i].dg_header != NULL; ++i)

if (*previous < header_table[i].dg_start

&& dp->result >= header_table[i].dg_start

&& dp->result < header_table[i + 1].dg_start)

{

digraph_header(_(header_table[i].dg_header));

break;

}

*previous = dp->result;

}

#endif

if (msg_col > Columns - list_width)

msg_putchar('\n');

if (msg_col)

while (msg_col % list_width != 0)

msg_putchar(' ');

p = buf;

*p++ = dp->char1;

*p++ = dp->char2;

*p++ = ' ';

*p = NUL;

msg_outtrans(buf);

p = buf;

if (has_mbyte)

{

if (enc_utf8 && utf_iscomposing(dp->result))

*p++ = ' ';

p += (*mb_char2bytes)(dp->result, p);

}

else

*p++ = (char_u)dp->result;

*p = NUL;

msg_outtrans_attr(buf, HL_ATTR(HLF_8));

p = buf;

if (char2cells(dp->result) == 1)

*p++ = ' ';

vim_snprintf((char *)p, sizeof(buf) - (p - buf), " %3d", dp->result);

msg_outtrans(buf);

}

}

{

curbuf->b_kmap_state &= ~KEYMAP_INIT;

if (*curbuf->b_p_keymap == NUL)

{

keymap_unload();

do_cmdline_cmd((char_u *)"unlet! b:keymap_name");

}

else

{

char_u *buf;

size_t buflen;

buflen = STRLEN(curbuf->b_p_keymap) + STRLEN(p_enc) + 14;

buf = alloc(buflen);

if (buf == NULL)

return e_outofmem;

vim_snprintf((char *)buf, buflen, "keymap/%s_%s.vim",

curbuf->b_p_keymap, p_enc);

if (source_runtime(buf, 0) == FAIL)

{

vim_snprintf((char *)buf, buflen, "keymap/%s.vim",

curbuf->b_p_keymap);

if (source_runtime(buf, 0) == FAIL)

{

vim_free(buf);

return N_("E544: Keymap file not found");

}

}

vim_free(buf);

}

return NULL;

}

{

char_u *line;

char_u *p;

char_u *s;

kmap_T *kp;

#define KMAP_LLEN 200 

char_u buf[KMAP_LLEN + 11];

int i;

char_u *save_cpo = p_cpo;

if (!getline_equal(eap->getline, eap->cookie, getsourceline))

{

emsg(_("E105: Using :loadkeymap not in a sourced file"));

return;

}

keymap_unload();

curbuf->b_kmap_state = 0;

ga_init2(&curbuf->b_kmap_ga, (int)sizeof(kmap_T), 20);

p_cpo = (char_u *)"C";

for (;;)

{

line = eap->getline(0, eap->cookie, 0, TRUE);

if (line == NULL)

break;

p = skipwhite(line);

if (*p != '"' && *p != NUL && ga_grow(&curbuf->b_kmap_ga, 1) == OK)

{

kp = (kmap_T *)curbuf->b_kmap_ga.ga_data + curbuf->b_kmap_ga.ga_len;

s = skiptowhite(p);

kp->from = vim_strnsave(p, (int)(s - p));

p = skipwhite(s);

s = skiptowhite(p);

kp->to = vim_strnsave(p, (int)(s - p));

if (kp->from == NULL || kp->to == NULL

|| STRLEN(kp->from) + STRLEN(kp->to) >= KMAP_LLEN

|| *kp->from == NUL || *kp->to == NUL)

{

if (kp->to != NULL && *kp->to == NUL)

emsg(_("E791: Empty keymap entry"));

vim_free(kp->from);

vim_free(kp->to);

}

else

++curbuf->b_kmap_ga.ga_len;

}

vim_free(line);

}

for (i = 0; i < curbuf->b_kmap_ga.ga_len; ++i)

{

vim_snprintf((char *)buf, sizeof(buf), "<buffer> %s %s",

((kmap_T *)curbuf->b_kmap_ga.ga_data)[i].from,

((kmap_T *)curbuf->b_kmap_ga.ga_data)[i].to);

(void)do_map(2, buf, LANGMAP, FALSE);

}

p_cpo = save_cpo;

curbuf->b_kmap_state |= KEYMAP_LOADED;

status_redraw_curbuf();

}

{

char_u buf[KMAP_MAXLEN + 10];

int i;

char_u *save_cpo = p_cpo;

kmap_T *kp;

if (!(curbuf->b_kmap_state & KEYMAP_LOADED))

return;

p_cpo = (char_u *)"C";

kp = (kmap_T *)curbuf->b_kmap_ga.ga_data;

for (i = 0; i < curbuf->b_kmap_ga.ga_len; ++i)

{

vim_snprintf((char *)buf, sizeof(buf), "<buffer> %s", kp[i].from);

(void)do_map(1, buf, LANGMAP, FALSE);

}

keymap_clear(&curbuf->b_kmap_ga);

p_cpo = save_cpo;

ga_clear(&curbuf->b_kmap_ga);

curbuf->b_kmap_state &= ~KEYMAP_LOADED;

status_redraw_curbuf();

}

{

int i;

kmap_T *kp = (kmap_T *)kmap->ga_data;

for (i = 0; i < kmap->ga_len; ++i)

{

vim_free(kp[i].from);

vim_free(kp[i].to);

}

}
