function.block:
{

memfile_T *mfp;

off_T size;

#if defined(STATFS) && defined(UNIX) && !defined(__QNX__) && !defined(__minix)

#define USE_FSTATFS

struct STATFS stf;

#endif

if ((mfp = ALLOC_ONE(memfile_T)) == NULL)

return NULL;

if (fname == NULL) 

{

mfp->mf_fname = NULL;

mfp->mf_ffname = NULL;

mfp->mf_fd = -1;

}

else

{

mf_do_open(mfp, fname, flags); 

if (mfp->mf_fd < 0)

{

vim_free(mfp);

return NULL;

}

}

mfp->mf_free_first = NULL; 

mfp->mf_used_first = NULL; 

mfp->mf_used_last = NULL;

mfp->mf_dirty = FALSE;

mfp->mf_used_count = 0;

mf_hash_init(&mfp->mf_hash);

mf_hash_init(&mfp->mf_trans);

mfp->mf_page_size = MEMFILE_PAGE_SIZE;

#if defined(FEAT_CRYPT)
mfp->mf_old_key = NULL;

#endif

#if defined(USE_FSTATFS)
if (mfp->mf_fd >= 0

&& fstatfs(mfp->mf_fd, &stf, sizeof(struct statfs), 0) == 0

&& stf.F_BSIZE >= MIN_SWAP_PAGE_SIZE

&& stf.F_BSIZE <= MAX_SWAP_PAGE_SIZE)

mfp->mf_page_size = stf.F_BSIZE;

#endif

if (mfp->mf_fd < 0 || (flags & (O_TRUNC|O_EXCL))

|| (size = vim_lseek(mfp->mf_fd, (off_T)0L, SEEK_END)) <= 0)

mfp->mf_blocknr_max = 0; 

else

mfp->mf_blocknr_max = (blocknr_T)((size + mfp->mf_page_size - 1)

/ mfp->mf_page_size);

mfp->mf_blocknr_min = -1;

mfp->mf_neg_count = 0;

mfp->mf_infile_count = mfp->mf_blocknr_max;

{

int shift = 10;

unsigned page_size = mfp->mf_page_size;

while (shift > 0 && (page_size & 1) == 0)

{

page_size = page_size >> 1;

--shift;

}

mfp->mf_used_count_max = (p_mm << shift) / page_size;

if (mfp->mf_used_count_max < 10)

mfp->mf_used_count_max = 10;

}

return mfp;

}

{

mf_do_open(mfp, fname, O_RDWR|O_CREAT|O_EXCL); 

if (mfp->mf_fd < 0)

return FAIL;

mfp->mf_dirty = TRUE;

return OK;

}

{

bhdr_T *hp, *nextp;

if (mfp == NULL) 

return;

if (mfp->mf_fd >= 0)

{

if (close(mfp->mf_fd) < 0)

emsg(_(e_swapclose));

}

if (del_file && mfp->mf_fname != NULL)

mch_remove(mfp->mf_fname);

for (hp = mfp->mf_used_first; hp != NULL; hp = nextp)

{

total_mem_used -= hp->bh_page_count * mfp->mf_page_size;

nextp = hp->bh_next;

mf_free_bhdr(hp);

}

while (mfp->mf_free_first != NULL) 

vim_free(mf_rem_free(mfp));

mf_hash_free(&mfp->mf_hash);

mf_hash_free_all(&mfp->mf_trans); 

vim_free(mfp->mf_fname);

vim_free(mfp->mf_ffname);

vim_free(mfp);

}

{

memfile_T *mfp;

linenr_T lnum;

mfp = buf->b_ml.ml_mfp;

if (mfp == NULL || mfp->mf_fd < 0) 

return;

if (getlines)

{

mf_dont_release = TRUE;

for (lnum = 1; lnum <= buf->b_ml.ml_line_count; ++lnum)

(void)ml_get_buf(buf, lnum, FALSE);

mf_dont_release = FALSE;

}

if (close(mfp->mf_fd) < 0) 

emsg(_(e_swapclose));

mfp->mf_fd = -1;

if (mfp->mf_fname != NULL)

{

mch_remove(mfp->mf_fname); 

VIM_CLEAR(mfp->mf_fname);

VIM_CLEAR(mfp->mf_ffname);

}

}

{

total_mem_used += new_size - mfp->mf_page_size;

mfp->mf_page_size = new_size;

}

{

bhdr_T *hp; 

bhdr_T *freep; 

char_u *p;

hp = mf_release(mfp, page_count);

freep = mfp->mf_free_first;

if (!negative && freep != NULL && freep->bh_page_count >= page_count)

{

if (freep->bh_page_count > page_count)

{

if (hp == NULL && (hp = mf_alloc_bhdr(mfp, page_count)) == NULL)

return NULL;

hp->bh_bnum = freep->bh_bnum;

freep->bh_bnum += page_count;

freep->bh_page_count -= page_count;

}

else if (hp == NULL) 

{

if ((p = alloc(mfp->mf_page_size * page_count)) == NULL)

return NULL;

hp = mf_rem_free(mfp);

hp->bh_data = p;

}

else 

{

freep = mf_rem_free(mfp);

hp->bh_bnum = freep->bh_bnum;

vim_free(freep);

}

}

else 

{

if (hp == NULL && (hp = mf_alloc_bhdr(mfp, page_count)) == NULL)

return NULL;

if (negative)

{

hp->bh_bnum = mfp->mf_blocknr_min--;

mfp->mf_neg_count++;

}

else

{

hp->bh_bnum = mfp->mf_blocknr_max;

mfp->mf_blocknr_max += page_count;

}

}

hp->bh_flags = BH_LOCKED | BH_DIRTY; 

mfp->mf_dirty = TRUE;

hp->bh_page_count = page_count;

mf_ins_used(mfp, hp);

mf_ins_hash(mfp, hp);

(void)vim_memset((char *)(hp->bh_data), 0,

(size_t)mfp->mf_page_size * page_count);

return hp;

}

{

bhdr_T *hp;

if (nr >= mfp->mf_blocknr_max || nr <= mfp->mf_blocknr_min)

return NULL;

hp = mf_find_hash(mfp, nr);

if (hp == NULL) 

{

if (nr < 0 || nr >= mfp->mf_infile_count) 

return NULL;

hp = mf_release(mfp, page_count);

if (hp == NULL && (hp = mf_alloc_bhdr(mfp, page_count)) == NULL)

return NULL;

hp->bh_bnum = nr;

hp->bh_flags = 0;

hp->bh_page_count = page_count;

if (mf_read(mfp, hp) == FAIL) 

{

mf_free_bhdr(hp);

return NULL;

}

}

else

{

mf_rem_used(mfp, hp); 

mf_rem_hash(mfp, hp);

}

hp->bh_flags |= BH_LOCKED;

mf_ins_used(mfp, hp); 

mf_ins_hash(mfp, hp); 

return hp;

}

{

int flags;

flags = hp->bh_flags;

if ((flags & BH_LOCKED) == 0)

iemsg(_("E293: block was not locked"));

flags &= ~BH_LOCKED;

if (dirty)

{

flags |= BH_DIRTY;

mfp->mf_dirty = TRUE;

}

hp->bh_flags = flags;

if (infile)

mf_trans_add(mfp, hp); 

}

{

vim_free(hp->bh_data); 

mf_rem_hash(mfp, hp); 

mf_rem_used(mfp, hp); 

if (hp->bh_bnum < 0)

{

vim_free(hp); 

mfp->mf_neg_count--;

}

else

mf_ins_free(mfp, hp); 

}

{

return __stdfiledes[filedescriptor];

}

{

int status;

bhdr_T *hp;

int got_int_save = got_int;

if (mfp->mf_fd < 0) 

{

mfp->mf_dirty = FALSE;

return FAIL;

}

got_int = FALSE;

status = OK;

for (hp = mfp->mf_used_last; hp != NULL; hp = hp->bh_prev)

if (((flags & MFS_ALL) || hp->bh_bnum >= 0)

&& (hp->bh_flags & BH_DIRTY)

&& (status == OK || (hp->bh_bnum >= 0

&& hp->bh_bnum < mfp->mf_infile_count)))

{

if ((flags & MFS_ZERO) && hp->bh_bnum != 0)

continue;

if (mf_write(mfp, hp) == FAIL)

{

if (status == FAIL) 

break;

status = FAIL;

}

if (flags & MFS_STOP)

{

if (ui_char_avail())

break;

}

else

ui_breakcheck();

if (got_int)

break;

}

if (hp == NULL || status == FAIL)

mfp->mf_dirty = FALSE;

if ((flags & MFS_FLUSH) && *p_sws != NUL)

{

#if defined(UNIX)

#if defined(HAVE_FSYNC)
if (STRCMP(p_sws, "fsync") == 0)

{

if (vim_fsync(mfp->mf_fd))

status = FAIL;

}

else

#endif

#if defined(__OPENNT) || defined(__TANDEM) || defined(__VOS__)

fflush(NULL);

#else

sync();

#endif

#endif

#if defined(VMS)
if (STRCMP(p_sws, "fsync") == 0)

{

if (vim_fsync(mfp->mf_fd))

status = FAIL;

}

#endif

#if defined(MSWIN)
if (_commit(mfp->mf_fd))

status = FAIL;

#endif

#if defined(AMIGA)
#if defined(__AROS__) || defined(__amigaos4__)

if (vim_fsync(mfp->mf_fd) != 0)

status = FAIL;

#else

#if defined(FEAT_ARP)
if (dos2)

#endif

#if defined(SASC)
{

struct UFB *fp = chkufb(mfp->mf_fd);

if (fp != NULL)

Flush(fp->ufbfh);

}

#else

#if defined(_DCC) || defined(__GNUC__) || defined(__MORPHOS__)

{

#if defined(__GNUC__) && !defined(__MORPHOS__) && defined(__libnix__)

extern unsigned long fdtofh(int filedescriptor);

#endif

#if !defined(__libnix__)

fflush(NULL);

#else

BPTR fh = (BPTR)fdtofh(mfp->mf_fd);

if (fh != 0)

Flush(fh);

#endif

}

#else 

Flush(_devtab[mfp->mf_fd].fd);

#endif

#endif

#endif

#endif 

}

got_int |= got_int_save;

return status;

}

{

bhdr_T *hp;

for (hp = mfp->mf_used_last; hp != NULL; hp = hp->bh_prev)

if (hp->bh_bnum > 0)

hp->bh_flags |= BH_DIRTY;

mfp->mf_dirty = TRUE;

}

{

mf_hash_add_item(&mfp->mf_hash, (mf_hashitem_T *)hp);

}

{

mf_hash_rem_item(&mfp->mf_hash, (mf_hashitem_T *)hp);

}

{

return (bhdr_T *)mf_hash_find(&mfp->mf_hash, nr);

}

{

hp->bh_next = mfp->mf_used_first;

mfp->mf_used_first = hp;

hp->bh_prev = NULL;

if (hp->bh_next == NULL) 

mfp->mf_used_last = hp;

else

hp->bh_next->bh_prev = hp;

mfp->mf_used_count += hp->bh_page_count;

total_mem_used += hp->bh_page_count * mfp->mf_page_size;

}

{

if (hp->bh_next == NULL) 

mfp->mf_used_last = hp->bh_prev;

else

hp->bh_next->bh_prev = hp->bh_prev;

if (hp->bh_prev == NULL) 

mfp->mf_used_first = hp->bh_next;

else

hp->bh_prev->bh_next = hp->bh_next;

mfp->mf_used_count -= hp->bh_page_count;

total_mem_used -= hp->bh_page_count * mfp->mf_page_size;

}

{

bhdr_T *hp;

int need_release;

buf_T *buf;

if (mf_dont_release)

return NULL;

need_release = ((mfp->mf_used_count >= mfp->mf_used_count_max)

|| (total_mem_used >> 10) >= (long_u)p_mmt);

if (mfp->mf_fd < 0 && need_release && p_uc)

{

FOR_ALL_BUFFERS(buf)

if (buf->b_ml.ml_mfp == mfp)

break;

if (buf != NULL && buf->b_may_swap)

ml_open_file(buf);

}

if (mfp->mf_fd < 0 || !need_release)

return NULL;

for (hp = mfp->mf_used_last; hp != NULL; hp = hp->bh_prev)

if (!(hp->bh_flags & BH_LOCKED))

break;

if (hp == NULL) 

return NULL;

if ((hp->bh_flags & BH_DIRTY) && mf_write(mfp, hp) == FAIL)

return NULL;

mf_rem_used(mfp, hp);

mf_rem_hash(mfp, hp);

if (hp->bh_page_count != page_count)

{

vim_free(hp->bh_data);

if ((hp->bh_data = alloc(mfp->mf_page_size * page_count)) == NULL)

{

vim_free(hp);

return NULL;

}

hp->bh_page_count = page_count;

}

return hp;

}

{

buf_T *buf;

memfile_T *mfp;

bhdr_T *hp;

int retval = FALSE;

FOR_ALL_BUFFERS(buf)

{

mfp = buf->b_ml.ml_mfp;

if (mfp != NULL)

{

if (mfp->mf_fd < 0 && buf->b_may_swap)

ml_open_file(buf);

if (mfp->mf_fd >= 0)

{

for (hp = mfp->mf_used_last; hp != NULL; )

{

if (!(hp->bh_flags & BH_LOCKED)

&& (!(hp->bh_flags & BH_DIRTY)

|| mf_write(mfp, hp) != FAIL))

{

mf_rem_used(mfp, hp);

mf_rem_hash(mfp, hp);

mf_free_bhdr(hp);

hp = mfp->mf_used_last; 

retval = TRUE;

}

else

hp = hp->bh_prev;

}

}

}

}

return retval;

}

{

bhdr_T *hp;

if ((hp = ALLOC_ONE(bhdr_T)) != NULL)

{

if ((hp->bh_data = alloc(mfp->mf_page_size * page_count)) == NULL)

{

vim_free(hp); 

return NULL;

}

hp->bh_page_count = page_count;

}

return hp;

}

{

vim_free(hp->bh_data);

vim_free(hp);

}

{

hp->bh_next = mfp->mf_free_first;

mfp->mf_free_first = hp;

}

{

bhdr_T *hp;

hp = mfp->mf_free_first;

mfp->mf_free_first = hp->bh_next;

return hp;

}

{

off_T offset;

unsigned page_size;

unsigned size;

if (mfp->mf_fd < 0) 

return FAIL;

page_size = mfp->mf_page_size;

offset = (off_T)page_size * hp->bh_bnum;

size = page_size * hp->bh_page_count;

if (vim_lseek(mfp->mf_fd, offset, SEEK_SET) != offset)

{

PERROR(_("E294: Seek error in swap file read"));

return FAIL;

}

if ((unsigned)read_eintr(mfp->mf_fd, hp->bh_data, size) != size)

{

PERROR(_("E295: Read error in swap file"));

return FAIL;

}

#if defined(FEAT_CRYPT)
if (*mfp->mf_buffer->b_p_key != NUL || mfp->mf_old_key != NULL)

ml_decrypt_data(mfp, hp->bh_data, offset, size);

#endif

return OK;

}

{

off_T offset; 

blocknr_T nr; 

bhdr_T *hp2;

unsigned page_size; 

unsigned page_count; 

unsigned size; 

if (mfp->mf_fd < 0 && !mfp->mf_reopen)

return FAIL;

if (hp->bh_bnum < 0) 

if (mf_trans_add(mfp, hp) == FAIL)

return FAIL;

page_size = mfp->mf_page_size;

for (;;)

{

int attempt;

nr = hp->bh_bnum;

if (nr > mfp->mf_infile_count) 

{

nr = mfp->mf_infile_count;

hp2 = mf_find_hash(mfp, nr); 

}

else

hp2 = hp;

offset = (off_T)page_size * nr;

if (hp2 == NULL) 

page_count = 1;

else

page_count = hp2->bh_page_count;

size = page_size * page_count;

for (attempt = 1; attempt <= 2; ++attempt)

{

if (mfp->mf_fd >= 0)

{

if (vim_lseek(mfp->mf_fd, offset, SEEK_SET) != offset)

{

PERROR(_("E296: Seek error in swap file write"));

return FAIL;

}

if (mf_write_block(mfp,

hp2 == NULL ? hp : hp2, offset, size) == OK)

break;

}

if (attempt == 1)

{

if (mfp->mf_fd >= 0)

close(mfp->mf_fd);

mfp->mf_fd = mch_open_rw((char *)mfp->mf_fname, mfp->mf_flags);

mfp->mf_reopen = (mfp->mf_fd < 0);

}

if (attempt == 2 || mfp->mf_fd < 0)

{

if (!did_swapwrite_msg)

emsg(_("E297: Write error in swap file"));

did_swapwrite_msg = TRUE;

return FAIL;

}

}

did_swapwrite_msg = FALSE;

if (hp2 != NULL) 

hp2->bh_flags &= ~BH_DIRTY;

if (nr + (blocknr_T)page_count > mfp->mf_infile_count)

mfp->mf_infile_count = nr + page_count;

if (nr == hp->bh_bnum) 

break;

}

return OK;

}

{

char_u *data = hp->bh_data;

int result = OK;

#if defined(FEAT_CRYPT)
if (*mfp->mf_buffer->b_p_key != NUL)

{

data = ml_encrypt_data(mfp, data, offset, size);

if (data == NULL)

return FAIL;

}

#endif

if ((unsigned)write_eintr(mfp->mf_fd, data, size) != size)

result = FAIL;

#if defined(FEAT_CRYPT)
if (data != hp->bh_data)

vim_free(data);

#endif

return result;

}

{

bhdr_T *freep;

blocknr_T new_bnum;

NR_TRANS *np;

int page_count;

if (hp->bh_bnum >= 0) 

return OK;

if ((np = ALLOC_ONE(NR_TRANS)) == NULL)

return FAIL;

freep = mfp->mf_free_first;

page_count = hp->bh_page_count;

if (freep != NULL && freep->bh_page_count >= page_count)

{

new_bnum = freep->bh_bnum;

if (freep->bh_page_count > page_count)

{

freep->bh_bnum += page_count;

freep->bh_page_count -= page_count;

}

else

{

freep = mf_rem_free(mfp);

vim_free(freep);

}

}

else

{

new_bnum = mfp->mf_blocknr_max;

mfp->mf_blocknr_max += page_count;

}

np->nt_old_bnum = hp->bh_bnum; 

np->nt_new_bnum = new_bnum;

mf_rem_hash(mfp, hp); 

hp->bh_bnum = new_bnum;

mf_ins_hash(mfp, hp); 

mf_hash_add_item(&mfp->mf_trans, (mf_hashitem_T *)np);

return OK;

}

{

NR_TRANS *np;

blocknr_T new_bnum;

np = (NR_TRANS *)mf_hash_find(&mfp->mf_trans, old_nr);

if (np == NULL) 

return old_nr;

mfp->mf_neg_count--;

new_bnum = np->nt_new_bnum;

mf_hash_rem_item(&mfp->mf_trans, (mf_hashitem_T *)np);

vim_free(np);

return new_bnum;

}

{

mfp->mf_ffname = FullName_save(mfp->mf_fname, FALSE);

}

{

if (mfp != NULL && mfp->mf_fname != NULL && mfp->mf_ffname != NULL)

{

vim_free(mfp->mf_fname);

mfp->mf_fname = mfp->mf_ffname;

mfp->mf_ffname = NULL;

}

}

{

return (mfp->mf_fname != NULL && mfp->mf_neg_count > 0);

}

{

#if defined(HAVE_LSTAT)
stat_T sb;

#endif

mfp->mf_fname = fname;

mf_set_ffname(mfp);

#if defined(MSWIN)

mf_fullname(mfp);

#endif

#if defined(HAVE_LSTAT)
if ((flags & O_CREAT) && mch_lstat((char *)mfp->mf_fname, &sb) >= 0)

{

mfp->mf_fd = -1;

emsg(_("E300: Swap file already exists (symlink attack?)"));

}

else

#endif

{

flags |= O_EXTRA | O_NOFOLLOW;

#if defined(MSWIN)
flags |= O_NOINHERIT;

#endif

mfp->mf_flags = flags;

mfp->mf_fd = mch_open_rw((char *)mfp->mf_fname, flags);

}

if (mfp->mf_fd < 0)

{

VIM_CLEAR(mfp->mf_fname);

VIM_CLEAR(mfp->mf_ffname);

}

else

{

#if defined(HAVE_FD_CLOEXEC)
int fdflags = fcntl(mfp->mf_fd, F_GETFD);

if (fdflags >= 0 && (fdflags & FD_CLOEXEC) == 0)

(void)fcntl(mfp->mf_fd, F_SETFD, fdflags | FD_CLOEXEC);

#endif

#if defined(HAVE_SELINUX) || defined(HAVE_SMACK)

mch_copy_sec(fname, mfp->mf_fname);

#endif

mch_hide(mfp->mf_fname); 

}

}

{

vim_memset(mht, 0, sizeof(mf_hashtab_T));

mht->mht_buckets = mht->mht_small_buckets;

mht->mht_mask = MHT_INIT_SIZE - 1;

}

{

if (mht->mht_buckets != mht->mht_small_buckets)

vim_free(mht->mht_buckets);

}

{

long_u idx;

mf_hashitem_T *mhi;

mf_hashitem_T *next;

for (idx = 0; idx <= mht->mht_mask; idx++)

for (mhi = mht->mht_buckets[idx]; mhi != NULL; mhi = next)

{

next = mhi->mhi_next;

vim_free(mhi);

}

mf_hash_free(mht);

}

{

mf_hashitem_T *mhi;

mhi = mht->mht_buckets[key & mht->mht_mask];

while (mhi != NULL && mhi->mhi_key != key)

mhi = mhi->mhi_next;

return mhi;

}

{

long_u idx;

idx = mhi->mhi_key & mht->mht_mask;

mhi->mhi_next = mht->mht_buckets[idx];

mhi->mhi_prev = NULL;

if (mhi->mhi_next != NULL)

mhi->mhi_next->mhi_prev = mhi;

mht->mht_buckets[idx] = mhi;

mht->mht_count++;

if (mht->mht_fixed == 0

&& (mht->mht_count >> MHT_LOG_LOAD_FACTOR) > mht->mht_mask)

{

if (mf_hash_grow(mht) == FAIL)

{

mht->mht_fixed = 1;

}

}

}

{

if (mhi->mhi_prev == NULL)

mht->mht_buckets[mhi->mhi_key & mht->mht_mask] = mhi->mhi_next;

else

mhi->mhi_prev->mhi_next = mhi->mhi_next;

if (mhi->mhi_next != NULL)

mhi->mhi_next->mhi_prev = mhi->mhi_prev;

mht->mht_count--;

}

{

long_u i, j;

int shift;

mf_hashitem_T *mhi;

mf_hashitem_T *tails[MHT_GROWTH_FACTOR];

mf_hashitem_T **buckets;

size_t size;

size = (mht->mht_mask + 1) * MHT_GROWTH_FACTOR * sizeof(void *);

buckets = lalloc_clear(size, FALSE);

if (buckets == NULL)

return FAIL;

shift = 0;

while ((mht->mht_mask >> shift) != 0)

shift++;

for (i = 0; i <= mht->mht_mask; i++)

{

vim_memset(tails, 0, sizeof(tails));

for (mhi = mht->mht_buckets[i]; mhi != NULL; mhi = mhi->mhi_next)

{

j = (mhi->mhi_key >> shift) & (MHT_GROWTH_FACTOR - 1);

if (tails[j] == NULL)

{

buckets[i + (j << shift)] = mhi;

tails[j] = mhi;

mhi->mhi_prev = NULL;

}

else

{

tails[j]->mhi_next = mhi;

mhi->mhi_prev = tails[j];

tails[j] = mhi;

}

}

for (j = 0; j < MHT_GROWTH_FACTOR; j++)

if (tails[j] != NULL)

tails[j]->mhi_next = NULL;

}

if (mht->mht_buckets != mht->mht_small_buckets)

vim_free(mht->mht_buckets);

mht->mht_buckets = buckets;

mht->mht_mask = (mht->mht_mask + 1) * MHT_GROWTH_FACTOR - 1;

return OK;

}
