function.block:
{

switch (fourcc)

{

case VLC_CODEC_I420:

case VLC_CODEC_YV12:

*type = VDP_CHROMA_TYPE_420;

*format = VDP_YCBCR_FORMAT_YV12;

break;

case VLC_CODEC_NV12:

*type = VDP_CHROMA_TYPE_420;

*format = VDP_YCBCR_FORMAT_NV12;

break;

case VLC_CODEC_I422:

*type = VDP_CHROMA_TYPE_422;

*format = VDP_YCBCR_FORMAT_YV12;

break;

case VLC_CODEC_NV16:

*type = VDP_CHROMA_TYPE_422;

*format = VDP_YCBCR_FORMAT_NV12;

break;

case VLC_CODEC_YUYV:

*type = VDP_CHROMA_TYPE_422;

*format = VDP_YCBCR_FORMAT_YUYV;

break;

case VLC_CODEC_UYVY:

*type = VDP_CHROMA_TYPE_422;

*format = VDP_YCBCR_FORMAT_UYVY;

break;

case VLC_CODEC_I444:

*type = VDP_CHROMA_TYPE_444;

*format = VDP_YCBCR_FORMAT_YV12;

break;

case VLC_CODEC_NV24:

*type = VDP_CHROMA_TYPE_444;

*format = VDP_YCBCR_FORMAT_NV12;

break;

default:

return false;

}

return true;

}

